import { useState } from "react";
import axios from "axios";
import { useEffect } from "react";

function App() {
  const [toDo, setTodo] = useState([]);
  const [title, setTitle] = useState(" ");
  const [Id, setId] = useState(" ");
  const addTodo = async () => {
    try {
      const { data } = await axios.post("http://localhost:8080/todo/add", {
        title,
      });
      if (data.success) {
        getallTodo();
        setTitle(" ");
      }
    } catch (error) {
      console.log(error);
    }
    // setTodo([...toDo, { id: Math.random(), title }]);
    // setTitle("");
  };
  const getallTodo = async () => {
    try {
      const { data } = await axios.get("http://localhost:8080/todo/get-all");
      // console.log(data);
      if (data.success) {
        setTodo(data.todo);
      }
    } catch (error) {
      console.log(error);
    }
  };
  const DeleteTodo = async (id) => {
    try {
      const { data } = await axios.delete(
        "http://localhost:8080/todo/delete/" + id
      );
      if (data.success) {
        getallTodo();
      }
    } catch (error) {
      console.log(error);
    }
  };
  const editTodo = async () => {};
  useEffect(() => {
    getallTodo();
  }, []);
  return (
    <div style={{ margin: "50px" }}>
      <h1>Todo List</h1>
      <hr />
      <div>
        <input
          type="text"
          placeholder="add item"
          onChange={(e) => {
            setTitle(e.target.value);
          }}
          value={title}
        />
      </div>
      <br />
      <button onClick={addTodo}>{id ? "Edit" : "Add"}</button>

      <div>
        {toDo.map((value) => (
          <p key={value.id}>
            {value.title}{" "}
            <button
              onClick={() => DeleteTodo(value.id)}
              style={{ height: "20px" }}
            >
              Delete
            </button>
            <button
              style={{ height: "20px" }}
              onClick={() => {
                setTitle(value.title);
                setId(value.id);
              }}
            >
              Edit
            </button>
          </p>
        ))}
      </div>
    </div>
  );
}

export default App;
