import { useState } from "react";

function App() {
  const [toDo, setTodo] = useState([]);
  const [title, setTitle] = useState("");
  const addTodo = () => {
    setTodo([...toDo, { id: Math.random(), title }]);
    setTitle("");
  };

  const DeleteTodo = (id) => {
    const new_todo = toDo.filter((val) => val.id !== id);
    setTodo(new_todo);
  };
  return (
    <div style={{ margin: "50px" }}>
      <h1>Todo List</h1>
      <hr />
      <div>
        {" "}
        <input type="text" placeholder="add item" />
      </div>
      <br />
      <button>add</button>

      <div style={{ display: "flex", alignItems: "center", gap: "20px" }}>
        <h3>Lorem 1</h3>
        <br />
        <button style={{ height: "20px" }}>Delete</button>
        <button style={{ height: "20px" }}>Edit</button>
      </div>
    </div>
  );
}

export default App;
